function vst_ajax_pagination_news()
{
    $post_type = $_POST['cpt'];
    $paged = $_POST['paged'];
    $load =  $_POST['load'];

    $query = new WP_Query(
      array(
        'post_type' => $post_type,
        'posts_per_page' => $load,
        'orderby' => 'date',
        'order' => 'DESC',
        'paged' => $paged
      )
    );

    switch ($post_type) {
      case 'news':
        $template = 'partials/modules/news_preview';
        break;

      default:
        $template = '';
        break;
    }

    if ($query->have_posts()) :
      header("HTTP/1.1 200 OK");
    while ($query->have_posts()): $query->the_post();
    echo '<div class="col medium-6">';
    get_template_part($template);
    echo '</div>';
    endwhile; else :
      header("HTTP/1.1 204 No Content");
    endif;

    die();
}

add_action('wp_ajax_nopriv_ajax_pagination_news', 'vst_ajax_pagination_news');
add_action('wp_ajax_ajax_pagination_news', 'vst_ajax_pagination_news');

function vst_ajax_pagination_press_release()
{
    $post_type = $_POST['cpt'];
    $load =  $_POST['load'];
    $year = $_POST['year'];

    $gbQuery = new WP_Query(
        array(
          'post_type' => 'press-release',
          'posts_per_page' => 1,
          'orderby' => 'date',
          'order' => 'DESC',
        )
      );
      $gb_exclude = wp_list_pluck( $gbQuery->posts, 'ID' );
      $meQuery = new WP_Query(
        array(
          'post_type' => 'press-release',
          'posts_per_page' => 4,
          'orderby' => 'date',
          'order' => 'DESC',
          'post__not_in' => $gb_exclude
        )
      );
      $me_exclude = wp_list_pluck( $meQuery->posts, 'ID' );

      $exclude_ids = array_merge($me_exclude, $gb_exclude);

    $query = new WP_Query(
        array(
          'post_type' => $post_type,
          'posts_per_page' => -1,
          'orderby' => 'date',
          'order' => 'DESC',
          'post__not_in' => $exclude_ids,
          'date_query' => array(
            array(
              'year'  => $year,
            ),
          ),
        )
      );

    if ($query->have_posts()) :
        header("HTTP/1.1 200 OK");
    while ($query->have_posts()): $query->the_post();
    echo '<div class="fetched-post">';
    get_template_part('partials/modules/press-room-archive-card');
    echo '</div>';
    endwhile; else:
        header("HTTP/1.1 204 No Content");
    endif;

    die();
}

  add_action('wp_ajax_nopriv_ajax_pagination_press_release', 'vst_ajax_pagination_press_release');
  add_action('wp_ajax_ajax_pagination_press_release', 'vst_ajax_pagination_press_release');

function amexgbt_language_list()
{
    $current_locale = get_locale();

    $current_blog_id = get_current_blog_id();
    $list = Mlp_Language_Nav_Menu_Data::get_list();

    $args = array(
        'public' => '1',
    );
    $site_query = new WP_Site_Query($args);

    $protocol = "http://";
    if (is_ssl()) {
        $protocol = "https://";
    }

    $html = '<ul class="lanuage-list">' . "\n";
    foreach ($site_query->sites as $site) {
        $url = $protocol . $site->domain . $site->path;
        $active_class = $site->blog_id == $current_blog_id ? "--active" : "";
        $html.= '<li class="language-list--item' . $active_class . '"><a href="'. $url .'" class="language-list--item__link">' . $list[$site->blog_id] . '</a></li>' . "\n";
    }
    $html .= '</ul>' . "\n";

    return $html;
}

