function country_list($options = array('name' => 'country', 'classes' => ''))
{
    $countries_json = file_get_contents(get_template_directory() . '/includes/countries.json');
    $countries = json_decode($countries_json);
    $select = '<select id="'. $options['name'] . '" name="' . $options['name'] . '" class="' . implode($options['classes'], ' ') . '">';
    $select .= '<option value="">'.__("Select Country...", "amexgbt").'</option>';
    if (count($countries) > 0) {
        foreach ($countries as $c) {
            $select .= '<option value="'. $c->code .'">'. $c->name .'</option>';
        }
    }
    $select .= '</select>';
    return $select;
}

