function vst_new_posts($post_id)
{
    $content = get_post_field('post_content', $post_id);
    $striped_content = wp_strip_all_tags($content, true);
    $content_words = str_word_count($striped_content, 0);
    $more_content = get_post_meta( $post_id);

    foreach ($more_content as $key => $value) {
        if (preg_match('/(_content_text)/', $key)) {
            $stripped_more_content .= wp_strip_all_tags($value[0]);
        }
    }

    $more_content_words = str_word_count($stripped_more_content, 0);
    if ($more_content_words > 0) {
        $rate = round($content_words / 200, 0);
        $more_content_rate = round($more_content_words  / 200, 0);
        $rate = $rate + $more_content_rate;

        if (! add_post_meta($post_id, 'vst_readtime', $rate, true)) {
            update_post_meta($post_id, 'vst_readtime', $rate);
        }
    } else {
        $rate = round($content_words / 200, 0);

        if (! add_post_meta($post_id, 'vst_readtime', $rate, true)) {
            update_post_meta($post_id, 'vst_readtime', $rate);
        }
    }


}

