function vst_create_social_link($vendor, $link, $title)
{
    $url = '';

    switch ($vendor) {
        case "twitter":
        $url = 'https://twitter.com/intent/tweet/?text='. rawurlencode($title) . '&url=' . urlencode($link);
        break;

        case "facebook":
        $url = 'http://www.facebook.com/sharer.php?u='. urlencode($link);
        break;

        case "linkedin":
        $url = 'https://www.linkedin.com/shareArticle?mini=true&url=' . urlencode($link) . '&title='. rawurlencode($title);
        break;

        default:
        $url = '';
    }

    return $url;
}

