function amex_change_search_url() {

    // Is search page
    if ( is_search() ) :

        $url = add_query_arg( array(
            'search' => get_query_var( 's' ),
        ), home_url( '/search/' ) . urlencode( get_query_var( 's' ) ) );
    endif;

    // is get param empty - then do redirect
    if ( is_search() && empty( $_GET[ 'search' ] ) ) {

        wp_redirect( $url );
        exit();
    }
}

add_action( 'template_redirect', 'amex_change_search_url' );

