/**
 * Add Dropdown Filter for Page Type (Page)
 *
 */
function amex_page_type_admin_filter(){
    global $post_type;

    if( $post_type === 'page' ) {

        $my_args = array(
            'show_option_all'   => 'All Page Types',
            'taxonomy'          => 'page_type',
            'orderby'           => 'name',
            'order'             => 'ASC',
            'name'              => 'page_type_admin_filter',
            'show_count'        => TRUE,
            'hide_empty'        => TRUE,
            'value_field'       => 'slug'
        );

        if( isset( $_GET['page_type_admin_filter'] ) ){
            $my_args['selected'] = sanitize_text_field($_GET['page_type_admin_filter']);
        }

        wp_dropdown_categories( $my_args );
    }
}
add_action('restrict_manage_posts', 'amex_page_type_admin_filter');

function amex_page_type_admin_filter_results( $query ) {
    global $post_type;

    if ( $post_type === 'page' ) {

        if ( isset( $_GET['page_type_admin_filter'] ) ){
            $term_slug = sanitize_text_field( $_GET['page_type_admin_filter'] );

            $query->query_vars['term'] = $term_slug;
            $query->query_vars['taxonomy'] = 'page_type';
        }
    }
}
add_action('pre_get_posts', 'amex_page_type_admin_filter_results');

